
// PDF.js Worker - Simple Implementation
console.log('PDF.js local worker starting...');

// Try to load the actual PDF.js worker
try {
  importScripts('https://unpkg.com/pdfjs-dist@5.3.31/build/pdf.worker.min.js');
  console.log('PDF.js worker loaded successfully from CDN');
} catch (error) {
  console.error('Failed to load PDF.js worker from CDN:', error);
  
  // Basic fallback - at least respond to messages
  self.onmessage = function(e) {
    console.error('PDF worker not available - processing will fail');
    self.postMessage({
      type: 'error',
      message: 'PDF worker failed to load'
    });
  };
}
